mapper.select:
select 
	
	     distinct
	
	
	 from xd_service_user 
	
		
	
	
		 where service_name like '%${serviceName}%'
	
	
	  limit ${pageStart},${pageSize}
	 
	
		order by ${orderByClause}

mapper.update:
update xd_service_user
set 
 
  service_password = #{servicePassword1} where service_phone = #{servicePhone}

update xd_service_user
set img = #{img,jdbcType=LONGVARBINARY}
where id = #{id,jdbcType=VARCHAR}

update xd_service_user
set lic = #{lic,jdbcType=LONGVARBINARY}
where id = #{id,jdbcType=VARCHAR}

update xd_service_user
set synopsis = #{synopsis,jdbcType=VARCHAR},
	worktime = #{worktime,jdbcType=VARCHAR},
	qq = #{qq,jdbcType=VARCHAR},
	custom_phone = #{customPhone,jdbcType=VARCHAR}
where service_phone = #{servicePhone,jdbcType=VARCHAR}

update xd_service_user
set status = #{status,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}
